public class SignOn
{
    

        Database db = Database.getInstance();
        
        String u = "";
        String p = "";
    
	public void doIt()
	{
            
            //Check signed in
            Account account = db.lookupAccount(u, false);
            if (account.checkSignedOn() != true){
                ask(u, p);
            }
            	
	}
        
        public void ask(String u, String p){
            
            //Connect to database
            
            
            //Ask for username
            
            System.out.println("Username: ");
            
            //Ask for password
            
            System.out.println("Password: ");
            
            //Use Account for temporary 

            
            //Check username exists in the database
            //Iterate through ArrayList to check if the username exists
            //Else RefuseSignOn
            Account account = db.lookupAccount(u, false);
            
            //Check password matches password in Account object
            if (p.equals(account.getPassword()))
            {
                account.signOn();
            } 
            else 
            {
            //Else RefuseSignOn
            RefuseSignOn.doIt();
            }
        }
}
